Dictionary<string, object> result = new Dictionary<string, object>();
            try
            {
                string erpCompany = string.Empty;
                EnumTipoPlataforma platformType = EnumTipoPlataforma.tpEmpresarial;

                if (context.Operation.DataSourceType.Equals("Primavera", StringComparison.InvariantCultureIgnoreCase))
                {
                    erpCompany = context.Operation.DataSource;
                }
                else
                {
                    if (!args.ContainsKey("Company"))
                        throw new Exception("Company was not sent ");
                    else
                        erpCompany = args["Company"].ToString();
                }

                string abvtApl = "";
                if (!args.ContainsKey("Module"))
                    throw new Exception("Module was not sent ");
                else
                    abvtApl = args["Module"].ToString();

                var httpClient = context.CreateApplicationHttpClient();
                var requestResult = httpClient.GetAsync($"Primavera/Default/{erpCompany}").Result;

                PrimaveraApplicationBehaviours.Primavera_EnsureSuccessApiResult(new Dictionary<string, object>
                {
                    {"requestResult", requestResult}
                }, context);

                PrimaveraDto primavera = requestResult.Content.ReadAsAsync<PrimaveraDto>().Result;
                Enum.TryParse(primavera.PlatformType, out platformType);
                var decryptArgs = new Dictionary<string, object> { { "EncryptedText", primavera.Password } };
                var decrypted = PrimaveraApplicationBehaviours.Primavera_Decrypt(decryptArgs);
                List<IDictionary<string, object>> entityList = new List<IDictionary<string, object>>();

                StdPlatBS platform = new StdPlatBS();
                var conf = new StdBSConfApl();
                conf.AbvtApl = abvtApl;
                conf.Instancia = "default";
                conf.Utilizador = primavera.Username;
                conf.PwdUtilizador = decrypted["DecryptedText"].ToString();
                conf.LicVersaoMinima = "9.00";

                platform.AbrePlataformaEmpresa(erpCompany, null, conf, platformType, string.Empty);

                result.Add("StdPlatBS", platform);
            }
            catch (Exception ex)
            {
                throw new Exception($"Error opening ERP Platform: {ex.Message}");
            }

            return result;
